DROP TABLE ITEM;
CREATE TABLE ITEM
(
    ITEM_NO            VARCHAR(20)  NOT NULL COMMENT '아이템번호',
    ITEM_NAME          VARCHAR(20) NOT NULL COMMENT '아이템이름',
    FIRST_CATEGORY_NO  NUMERIC(10) NOT NULL COMMENT '첫 번째 카테고라',
    SECOND_CATEGORY_NO NUMERIC(10) NOT NULL COMMENT '두 번째 카테고라',

    CONSTRAINT PK_ITEM
        PRIMARY KEY (ITEM_NO, ITEM_NAME),
    CONSTRAINT FK_ITEM_FIRST_CATEGORY_NO
        FOREIGN KEY (FIRST_CATEGORY_NO) REFERENCES FIRST_CATEGORY (FIRST_CATEGORY_NO),
    CONSTRAINT FK_ITEM_SECOND_CATEGORY_NO
        FOREIGN KEY (SECOND_CATEGORY_NO) REFERENCES SECOND_CATEGORY (SECOND_CATEGORY_NO)
)
COMMENT '아이템';

SELECT * FROM ITEM;

